(function(){var scriptList=['lib/jquery.min.js','lib/web3.min.js','lib/pxsdk.min.js'];function loadSingleScript(src,callback){var s=document.createElement('script');s.async=false;s.src=src;s.addEventListener('load',function(){s.removeEventListener('load',arguments.callee,false);callback()},false);var indexDev=document.getElementById("pxLib");indexDev.parentNode.insertBefore(s,indexDev)};function loadSingleCss(src,callback){var link=document.createElement("link");link.async=false;link.type="text/css";link.rel="stylesheet";link.href=src;link.addEventListener('load',function(){link.removeEventListener('load',arguments.callee,false);callback()},false);document.querySelector("head").appendChild(link)};function loadStart(list,callback){var loaded=0;var len=list.length;var call=function(){loaded++;if(loaded>=len){callback()}else{loadNext()}};var loadNext=function(){var isScript=/.*(.js)$/;var wantUrl=list[loaded];if(isScript.test(wantUrl)){loadSingleScript(wantUrl,call)}else{loadSingleCss(wantUrl,call)}};loadNext()};loadStart(scriptList,function(){if(typeof window.ethereum!=="undefined"){window.provider=web3.currentProvider}})})();